RenderProp:
import { Switch } from "@ark-ui/solid";

const RenderProp = () => (
  <Switch.Root>
    {(api) => (
      <>
        <Switch.Control>
          <Switch.Thumb />
        </Switch.Control>
        <Switch.Label>
          Feature is {api().isChecked ? "enabled" : "disabled"}
        </Switch.Label>
      </>
    )}
  </Switch.Root>
);
